import React, { Component } from "react";
import { Image } from "react-native";
import {
  Container,
  H1,
  View,
  DeckSwiper,
  Card,
  CardItem,
  Thumbnail,
  Text,
  Left,
  Right,
  Body,
  Icon,
  Row
} from "native-base";

const cards = [
  {
    place: "Oaxaca,Oaxaca",
    stars: "4.5",
    image: require("./img/image1.jpg"),
    text:
      "lorem ipsum Lorem, ipsum dolor sit amet consectetur adipisicing elite "
  }
];
export default class SwipCards extends Component {
  render() {
    return (
      <Container>
        <View>
          <DeckSwiper
            dataSource={cards}
            renderItem={item => (
              <Card
                style={{
                  elevation: 3,
                  border: "solid",
                  borderRadius: "10px",
                  padding: "20px",
                  color: "#4D5E73"
                }}
              >
                <H1 style={{ padding: "10px", fontSize: '20px' }}>Conoce Oaxaca!!!</H1>

                <CardItem cardBody>
                  <Image
                    source={item.image}
                    style={{ height: 200, flex: 1, padding: "20px", borderTopLeftRadius: '15px', borderTopRightRadius: '15px', }}
                  />
                </CardItem>
                <CardItem
                  style={{
                    flex:'1',

                    backgroundColor: "gray",
                    borderBottomLeftRadius: "15px",
                    borderBottomRightRadius: "15px",
                    flexDirection: 'column'
                  }}
                >
                  <Body style={{
                    flexDirection: "column",

                  }}>
                    <Text>
                      {item.text}
                    </Text>
                  </Body>
                  <Left
                    style={{
                    alignSelf:'flex-start',

                    flex:'3',

                     }}
                  >
                    <Icon
                      style={{ color: "#4D5E73", position: "relative" }}
                      active
                      name="navigate"
                    />
                    <Text>{item.place}</Text>
                  </Left>
                  <Right style={{

                    alignSelf:'flex-end',flexDirection: "row"

                  }}  >
                    <Icon
                      style={{
                        color: "#25EAA8",
                      }}
                      active
                      name="star"
                    />
                    <Text>{item.stars}</Text>
                  </Right>
                </CardItem>
              </Card>
            )}
          />
        </View>
      </Container>
    );
  }
}
